parameters.text:
=Candidate Resume: {{ $json.user_data.resume }}

Candidate Job Criterias : {{ $json.user_data.filters }}

Job Description: {{ $json.job_offer }}

parameters.inputSchema:
{
  "name": "resume_screening_evaluation",
  "description": "Analyzes a candidate's resume against a job description and outputs requirements, required skills, matched skills, strengths, weaknesses, and an overall fit score.",
  "type": "object",
  "properties": {
    "requirements": {
      "type": "array",
      "description": "The 4 most important requirements (experience level, knowledge, business, etc.) mentioned in the job offer description.",
      "items": {
        "type": "string"
      },
      "minItems": 4,
      "maxItems": 4
    },
    "required_skills": {
      "type": "array",
      "description": "List of all technical and business skills required based on the job description.",
      "items": {
        "type": "string"
      }
    },
    "candidate_matched_skills": {
      "type": "array",
      "description": "List of all technical and business required skills that match the candidate's resume and stack filters.",
      "items": {
        "type": "string"
      }
    },
    "candidate_strengths": {
      "type": "array",
      "description": "Top strengths or relevant qualifications the candidate brings to the table. Be specific.",
      "items": {
        "type": "string"
      }
    },
    "candidate_weaknesses": {
      "type": "array",
      "description": "Areas where the candidate is lacking or mismatched based on the job description.",
      "items": {
        "type": "string"
      }
    },
    "overall_fit_rating": {
      "type": "integer",
      "description": "A percentage score between 0 (terrible match) and 100 (perfect match) representing overall candidate fit. No decimals.",
      "minimum": 0,
      "maximum": 100
    },
    "rating_justification": {
      "type": "string",
      "description": "Clear explanation to the candidate of why they received this score, referencing specific resume content and alignment with the job description. Keep it between 4 and 5 short phrases maximum."
    }
  },
  "required": [
    "requirements",
    "required_skills",
    "candidate_matched_skills",
    "candidate_strengths",
    "candidate_weaknesses",
    "overall_fit_rating",
    "rating_justification"
  ]
}

parameters.jsCode:
// Node Code - Format Job Data
const items = $input.all();

return items.map(item => {
  const data = item.json;

  return {
    json: {
      job_offer: {
        id: data.id,
        title: data.title,
        company: data.company,
        location: data.location,
        type: data.type,
        salary: data.salary,
        description: data.description,
        experience: data.experience,
        postedDate: data.postedDate,
        source: data.source,
        url: data.url
      },
      user_data: {
        user_id: data.user_id,
        resume: data.resume,
        filters: data.filters
      }
    }
  };
});
